INSERT INTO `productos` VALUES(331, 'Filtros K&N', 'Reemplazos originales.', 'Diapositiva3 (2).JPG', 'imagesaa.jpg', 'imagess.jpg', '<h1 style="text-align: center;">\r\n	<span style="font-size: 48px;"><span style="font-family: verdana,geneva,sans-serif;"><span style="color: rgb(178, 34, 34);"><span style=""><span style="background-color: rgb(255, 255, 255);">Filtros Originales K&amp;N</span></span></span></span></span></h1>\r\n<h1 style="text-align: center;">\r\n	<span style="font-size: 36px;"><span style="font-family: verdana,geneva,sans-serif;"><span style="color: rgb(178, 34, 34);"><span style=""><img alt="" src="/admini/ckfinder/userfiles/images/kyn.jpg" style="width: 311px; height: 90px;" /></span></span></span></span></h1>\r\n<p>\r\n	<span style="font-size: 36px;"><span style="font-family: verdana,geneva,sans-serif;"><span style="color: rgb(178, 34, 34);"><span style=""><span style="background-color: rgb(255, 255, 255);">Por qu&eacute; instalarle Filtros K&amp;N a tu veh&iacute;culo:</span></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style="color: rgb(178, 34, 34);"><span style=""><span style="background-color: rgb(255, 255, 255);">A diferencia de los filtros convencionales cuyo material es de papel, los Filtros K&amp;N est&aacute;n hechos de algod&oacute;n, recubierto&nbsp;con rejillas met&aacute;licas que fucionados con un aceite especial ofrec&eacute;n mayor eficiencia en su funci&oacute;n principal: </span></span></span><span style="color: rgb(255, 0, 0);"><span style=""><span style="background-color: rgb(255, 255, 255);">PROVEER DE AIRE PURO Y LIBRE DE PART&Iacute;CULAS AL MOTOR DE SU VEH&Iacute;CULO.</span></span></span></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style="color: rgb(255, 0, 0);"><span style=""><span style="background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=""><img alt="" src="/admini/ckfinder/userfiles/images/imagesaa.jpg" style="width: 438px; height: 261px;" /></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 26px;"><strong><span style="font-family: verdana,geneva,sans-serif;"><span><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">Ventajas de los Filtros K&amp;N:</span></span></span></span></strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">-Mayor flujo de aire.</span></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">-Mayor duraci&oacute;n del combustible o gasolina.</span></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">-Mejor torque.</span></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">-Menos gastos en mantenimiento.</span></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">-Mayor duraci&oacute;n.</span></span></span></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">Los Filtros K&amp;N tienen a dem&aacute;s otra gran ventaja: Son lavables y reusables! Si, puedes darle mantenimiento a tus filtros con un Kit Especial de la misma marca K&amp;N, el cual incluye un Champ&uacute; y un Aceite el cual rinde para un uso de 3 a 4 lavadas.</span></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 20px;"><span style="font-family: verdana,geneva,sans-serif;"><span style=""><span style="color: rgb(165, 42, 42);"><span style="background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><img alt="" src="/admini/ckfinder/userfiles/images/imagesCA1MMS3Y.jpg" style="width: 418px; height: 334px;" /></span></span></span></span></p>\r\n<p>\r\n	<span style="font-size: 28px;"><strong><span style="color: rgb(255, 0, 0);"><span style="font-family: verdana,geneva,sans-serif;"><span><span style="background-color: rgb(255, 255, 255);">No lo pienses m&aacute;s.! Los Filtros K&amp;N son lo &uacute;ltimo en la tecnolog&iacute;a automotr&iacute;z.</span></span></span></span></strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>', 2, 3, 'Nuevo', 600.00, '2012-01-23', 1, 7, 17, 313, 0, '2', 0, '2011-12-23 14:36:46', 73, '2015-08-21 08:21:13', '0', '', '');
